import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';

@Component({
  selector: 'app-vacations',
  templateUrl: './vacations.component.html',
  styleUrls: ['./vacations.component.css'],
})
export class VacationsComponent implements OnInit {
  vacations: any;
  showRegisterModal: any | undefined;
  showLoginModal: any | undefined;
  showNewVacationModal: any | undefined;
  nameOfUser: any | undefined;
  modeUser: any | undefined;
  modeAdmin: any | undefined;

  constructor(private http: HttpClient, private stateService: StateService) {}

  ngOnInit() {
    this.http.get('http://localhost:8080/vacations').subscribe((data) => {
      this.vacations = data;
    });

    this.stateService.checkLogin();

    this.stateService.currentModeUser.subscribe((value) => {
      this.modeUser = value;
    });
    this.stateService.currentModeAdmin.subscribe((value) => {
      this.modeAdmin = value;
    });

    this.stateService.currentShowRegisterModal.subscribe((value) => {
      this.showRegisterModal = value;
    });
    this.stateService.currentShowLoginModal.subscribe((value) => {
      this.showLoginModal = value;
    });
    this.stateService.currentShowNewVacationModal.subscribe((value) => {
      this.showNewVacationModal = value;
    });
    this.stateService.currentNameOfUser.subscribe((value) => {
      this.nameOfUser = value;
    });
    if (this.nameOfUser !== 'Guest') {
      this.stateService.changeShowLoginModal(false);
    }
  }
  loggin() {
    this.stateService.changeShowLoginModal(true);
    this.stateService.changeModeUser(false);
    this.stateService.changeModeAdmin(false);
    Cookies.remove('token');
  }

  logout() {
    this.stateService.changeShowLoginModal(true);
    this.stateService.changeModeUser(false);
    this.stateService.changeModeAdmin(false);
    Cookies.remove('token');
  }

  addNewVacation(){
    this.stateService.changeShowLoginModal(false);
    this.stateService.changeModeUser(false);
    this.stateService.changeShowNewVacationModal(true);

  }





}
<div *ngIf="vacations">
  <app-login *ngIf="showLoginModal && !showNewVacationModal && !showRegisterModal "></app-login>
  <app-register *ngIf="showRegisterModal && !showLoginModal && !showNewVacationModal"></app-register>
  <app-new-vacation *ngIf="showNewVacationModal && !showLoginModal && !showRegisterModal"></app-new-vacation>

  <div id="header">
    <ng-container *ngIf="modeUser; else guest">
      <div class="user-guest-div">
        <p class="hello-login-loguot">Hello, {{nameOfUser}}...</p>
        <button type="button" class="logout-btn" (click)="logout()">Logout</button>
      </div>
    </ng-container>
    <ng-template #guest>
      <ng-container *ngIf=" !modeUser && !modeAdmin && !showLoginModal; else admin">
        <div class="user-guest-div">
          <p class="hello-login-loguot">Hello, {{nameOfUser}}...</p>
          <button type="button" class="loggin-btn" (click)="loggin()">Loggin</button>
        </div>
      </ng-container>
    </ng-template>
    <ng-template #admin>
      <ng-container *ngIf="modeAdmin">
        <div class="user-guest-div">
          <p class="hello-login-loguot">Hello, {{nameOfUser}}... (Admin)</p>
          <button type="button" class="logout-btn" (click)="logout()">Logout</button>
          <button type="button" class="logout-btn" (click)="addNewVacation()">Add New Vacation</button>
        </div>
      </ng-container>
    </ng-template>
    <div id="topHeader">monster Vaction</div>
  </div>

  <p >Hello {{nameOfUser}}</p>
  <div id="container" *ngIf="!showLoginModal && !showRegisterModal && !showNewVacationModal">
    <div class="menu-item" *ngFor="let vacation of vacations">
      <img class="img-menu" src="{{ vacation.img_url }}" alt="" />
      <h1 class="menu-item-header">
        {{ vacation.title }} in {{ vacation.destination }}
      </h1>
      <p class="menu-date">
        From
        {{ vacation.date_start.slice(0, vacation.date_start.length - 14) }} To
        {{ vacation.date_end.slice(0, vacation.date_end.length - 14) }}
      </p>
      <p class="item-text">{{ vacation.description }}</p>
      <p class="price">Just $ {{ vacation.price }}</p>
      <p class="admin-delete-icon">Del</p>
      <p class="admin-edit-icon">Edit</p>
    </div>
  </div>
</div>
