import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Input, Output, EventEmitter } from '@angular/core';


@Component({
  selector: 'app-vacations',
  templateUrl: './vacations.component.html',
  styleUrls: ['./vacations.component.css']
})
export class VacationsComponent implements OnInit {
  @Input() vacations: any;
  @Input() modeUser: boolean = false;
  @Input() modeAdmin: boolean = false;
  @Input() showLoginModal: boolean = false;
  @Input() isLogin: boolean = false;
  @Input() showNewVacationModal: boolean = false;
  @Input() showVacationUpdate: boolean = false;
  @Input() showVacationDelete: boolean = false;
  @Input() showRegiterModal: boolean = false;
  @Input() nameOfUser: string = 'Guest';



  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.http.get('https://react-node-mysql-jwt.herokuapp.com/vacations')
      .subscribe(data => {
        this.vacations = data;
      });
  }
}
