import { Component } from '@angular/core';
import Cookies from 'js-cookie';
import axios from 'axios';
import { Input, Output, EventEmitter } from '@angular/core';
import { SharedService } from '../service/shared.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  user: string | undefined;
  password: string | undefined;

  constructor(private sharedService: SharedService) {}




  onSubmit() {
    const data = { userName: this.user, password: this.password };
    console.log(`Username: ${this.user} Password: ${this.password}`);
    Cookies.remove('token');
    axios
      .post('https://react-node-mysql-jwt.herokuapp.com/login', {
        data,
      })
      .then(function (response) {
        const { accessToken, userName, admin } = response.data;
        console.log(response.data);
        Cookies.set('token', accessToken, { expires: 0.1 });
        if (response.status === 200) {
          // setMessage('Successfully logged in')

          if (admin) {
            // setModeAdmin(true)
            // setModeUser(false)
            // setNameOfUser(userName)
          }
          if (!admin) {
            // setModeUser(true)
            // setModeAdmin(false)
            // setNameOfUser(userName)
          }
        }
      })
      .catch(function (error) {
        // setMessage(error.response.data)
      });
  }

  handleRegister() {
    // this.sharedService.showRegiterModal = true;
    // this.showLoginModal = false;
    console.log(this.sharedService.showRegisterModal)
  }
}
<div>
  <form class="form-login" (ngSubmit)="onSubmit()">
    <h3 class="form-login-header">Login</h3>
    <input
      class="form-login-input"
      type="text"
      [(ngModel)]="user"
      name="user"
      placeholder="User"
    />
    <input
      class="form-login-input"
      type="password"
      [(ngModel)]="password"
      name="password"
      placeholder="Password"
    />
    <button class="form-login-submitBtn" type="submit">Login</button>
    <input
      value="Not A User ? Register Now"
      type="button"
      class="form-login-registerBtn"
      (click)="handleRegister()"
    />
  </form>
</div>
