import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'tracate',
})
export class TracatePipe implements PipeTransform {
  transform(value: any): any {
    return value.split(' ').slice(0,2).join(' ') + '...';
  }
}
