import { Component } from '@angular/core';
import { Input, Output, EventEmitter } from '@angular/core';
import axios from 'axios';
import { StateService  } from '../service/shared.service';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss'],
})
export class RegisterComponent {
  firstName: string | undefined;
  lastName: string | undefined;
  userName: string | undefined;
  passwordVerification: string | undefined;
  password: string | undefined;

  submitRegister(): void {
    const data: any = {
      firstName: this.firstName,
      lastName: this.lastName,
      userName: this.userName,
      password: this.password,
      passwordVerification: this.passwordVerification,
    };
    console.log(data);

    if (
      !data.firstName ||
      !data.lastName ||
      !data.userName ||
      data.password !== data.passwordVerification
    ) {
      return alert('Verification Failed');
    }
    if (data.password === data.passwordVerification) {
      axios
        .post('http://localhost:8080/register', {
          data,
        })
        .then(function (response) {
          console.log(response.data);
          alert(response.data)


            // setOpenRegisterModal(false)
            // setOpenLoginModal(true)

        })
        .catch(function (error) {
          // setMessage(error.response.data)
        });
    }
  }

  closeModal() {
    this.stateService.changeShowLoginModal(false);


}
<div id="form-register-container">
    <form id="form-register">
      <button
      id="form-register-closeModal-icon"
      type="button"
      (click)="closeModal()"
    >
      X
    </button>
      <!-- <fa-icon id="form-register-closeModal-icon" icon="fa-window-close" (click)="setOpenRegisterModal(false)"></fa-icon> -->
      <h3 id="form-register-header">Register</h3>
      <label class="form-register-lable"></label>
      <input
        class="form-register-input"
        type="text"
        name="firstName"
        placeholder="First Name "
        [(ngModel)]="firstName"
      />
      <!-- <p *ngIf="errors?.firstName">{{errors.firstName.message}}</p> -->
      <label class="form-register-lable"></label>
      <input
        class="form-register-input"
        type="text"
        name="lastName"
        placeholder="Last Name"
        [(ngModel)]="lastName"
      />
      <!-- <p *ngIf="errors?.lastName">{{errors.lastName.message}}</p> -->
      <label class="form-register-lable"></label>
      <input
        class="form-register-input"
        type="text"
        name="userName"
        placeholder="User Name"
        [(ngModel)]="userName"
      />
      <!-- <p *ngIf="errors?.userName">{{errors.userName.message}}</p> -->
      <label class="form-register-lable"></label>
      <input
        class="form-register-input"
        type="password"
        name="password"
        placeholder="Password"
        [(ngModel)]="password"
      />
      <!-- <p *ngIf="errors?.password">{{errors.password.message}}</p> -->
      <label class="form-register-lable"></label>
      <input
        class="form-register-input"
        type="password"
        name="passwordVerification"
        placeholder="Password Verification"
        [(ngModel)]="passwordVerification"


      />
      <!-- <p *ngIf="errors?.passwordVerification">{{errors.passwordVerification.message}}</p> -->
      <input id="form-register-submitBtn" value="Register" type="submit" (click)="submitRegister()" />
      <!-- <h4 id="message-after-register" *ngIf="message">{{message}}</h4> -->
    </form>
  </div>
