import { Component } from '@angular/core';
import Cookies from 'js-cookie'

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css'],
})
export class LoginComponent {
  username: string | undefined;
  password: string | undefined;

  onSubmit() {
    console.log(`Username: ${this.username} Password: ${this.password}`);
    Cookies.remove('token');




  }
}

const submitLogin = (data) => {

  axios
    .post('https://react-node-mysql-jwt.herokuapp.com/login', {
      data,
    })
    .then(function (response) {
      const { accessToken, userName, admin } = response.data
      Cookies.set('token', accessToken, { expires: 0.1 })
      if (response.status === 200) {
        // setMessage('Successfully logged in')


          if (admin) {
            setModeAdmin(true)
            setModeUser(false)
            setNameOfUser(userName)
          }
          if (!admin) {
            setModeUser(true)
            setModeAdmin(false)
            setNameOfUser(userName)
          }

      }
    })
    .catch(function (error) {
      setMessage(error.response.data)
    })
}

  <form class="form-login" (ngSubmit)="onSubmit()">
    <h3 class="form-login-header">Login</h3>
    <input
      class="form-login-input"
      type="text"
      [(ngModel)]="username"
      name="username"
      placeholder="Username"
    />
    <input
      class="form-login-input"
      type="password"
      [(ngModel)]="password"
      name="password"
      placeholder="Password"
    />
    <button class="form-login-submitBtn" type="submit">Login</button>
    <input
      value="Not A User ? Register Now"
      type="button"
      class="form-login-registerBtn"
    />
  </form>
