import { Component, Input } from '@angular/core';
import axios from 'axios';
import { StateService  } from '../service/shared.service';
import Cookies from 'js-cookie';


@Component({
  selector: 'app-delete-vacation',
  templateUrl: './delete-vacation.component.html',
  styleUrls: ['./delete-vacation.component.scss']
})
export class DeleteVacationComponent {
  @Input() vacation: any;
  id: string | undefined;

  constructor(private stateService: StateService) {}


  deleteRow(id: any){
     id = this.vacation.id



  }

  const deleteRow = (id:) => {
    const jwt = Cookies.get('token')
    axios
      .delete(`https://react-node-mysql-jwt.herokuapp.com/delete/${id}`, {
        withCredentials: true,
        headers: { authorization: `Bearer ${jwt}` },
        id,
      })
      .then(function (response) {
        setMessage(response.data)
        if (response.status === 200) {
          setTimeout(() => {
            setOpenDeleteVacationModal(false)
          }, 1500)
        }
      })
      .catch(function (error) {
        setMessage(error.response.data)
        console.log(error.response.data)
      })




}
