import { Injectable } from '@angular/core';

@Injectable()
export class SharedService {
  public sharedVariable: any;
  vacations: any;
  modeUser = false;
  modeAdmin: boolean = false;
  showLoginModal: boolean = true;
  isLogin: boolean = false;
  showNewVacationModal: boolean = false;
  showVacationUpdate: boolean = false;
  showVacationDelete: boolean = false;
  showRegiterModal: boolean = false;
  nameOfUser: string = 'Guest';
  constructor() {}
}
