import { Component, Input } from '@angular/core';
import axios from 'axios';
import { StateService } from '../service/shared.service';
import Cookies from 'js-cookie';

@Component({
  selector: 'app-delete-vacation',
  templateUrl: './delete-vacation.component.html',
  styleUrls: ['./delete-vacation.component.scss'],
})
export class DeleteVacationComponent {
  @Input() vacation: any;
  // id: string | undefined;

  constructor(private stateService: StateService) {}

  deleteRow = () => {
    const jwt = Cookies.get('token');
    axios
      .delete(`http://localhost:8080/update/delete/${this.vacation.id}`, {
        withCredentials: true,
        headers: { authorization: `Bearer ${jwt}` },
      })
      .then(function (response) {
        alert(response.data);
        if (response.status === 200) {
        }
      })
      .catch(function (error) {
        alert(error.response.data);
        console.log(error.response.data);
      });
  };

  closeModal() {
    this.stateService.changeShowVacationDelete(false);
  }
}
<div id="form-delete-container">
  <form id="form-delete">
    <button id="close-delete-modal-icon" (click)="closeModal()">X</button>
    <h3 id="form-delete-header">Delete vacation</h3>
    <button id="form-delete-btn" (click)="deleteRow()">Delete</button>
  </form>
</div>
